// Function to rescale a vector of numbers to unit interval
vector<float> rescale_to_unit(const vector<float>& numbers) {
    float min = numeric_limits<float>::max(), max = numeric_limits<float>::lowest();
    for (auto it = numbers.begin(); it != numbers.end(); ++it) {
        if (*it < min) min = *it;
        if (*it > max) max = *it;
    }
    for (auto it = numbers.begin(); it != numbers.end(); ++it) {
        *it = (*it - min) / (max - min);
    }
    return numbers;
}

