std::string flip_case(const std::string& str) {
    std::string out;
    for (auto c : str) {
        if (c >= 'a' && c <= 'z') {
            out += static_cast<char>(c - 32);
        } else if (c >= 'A' && c <= 'Z') {
            out += static_cast<char>(c + 32);
        } else {
            out += c;
        }
    }
    return out;
}

