bool isPrime(int num) {
    if (num == 1) return false;
    for (int i = 2; i <= sqrt(num); i++) {
        if (num % i == 0) return false;
    }
    return true;
}

int primeFib(int n) {
    int fibNums[] = {1, 2};
    int count = 2;
    while (count < n) {
        int nextFib = fibNums[count - 1] + fibNums[count - 2];
        if (isPrime(nextFib)) {
            fibNums[count++] = nextFib;
        }
    }
    return fibNums[n - 1];
}

