const findClosestElements = (numbers) => {
  var closestPair = null;
  var distance = null;

  for (let i = 0; i < numbers.length; i++) {
    for (let j = i + 1; j < numbers.length; j++) {
      let a = numbers[i], b = numbers[j];
      let newDistance = Math.abs(a - b);

      if (closestPair === null || newDistance < distance) {
        distance = newDistance;
        closestPair = [Math.min(a, b), Math.max(a, b)];
      }
    }
  }

  return closestPair;
};

